import { tags } from "typia";

/**
 * Generic type to represent confirmation/status for soft-deletion requests.
 *
 * Used throughout the API for confirming soft-delete outcomes, indicating both
 * the target and the operation status.
 *
 * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
 */
export type IDeleteConfirmation = {
  /**
   * Whether or not the delete/soft delete action succeeded.
   *
   * True if the deletion was performed, false if not (e.g., already deleted
   * or permission denied).
   */
  deleted: boolean;

  /**
   * Unique identifier (UUID) of the record that deletion was attempted on.
   *
   * Corresponds to the primary key of the entity being deleted.
   */
  id: string & tags.Format<"uuid">;
};
