import { IPage } from "./IPage";
import { IFavorite } from "./IFavorite";

/**
 * Paginated response object for favorites.
 *
 * Encapsulates a set of favorite entries according to filter/search/pagination
 * criteria using the standard IPage<T> pattern described in requirements. Used
 * for dashboard listing, notification/engagement features, and personal/user
 * workspace APIs.
 *
 * This mirrors the 'IPage<T>' standard structure, with 'pagination' metadata
 * and an array of IFavorite records as data.
 *
 * @autobe Generated by AutoBE - https://github.com/wrtnlabs/autobe
 */
export type IPageIFavorite = {
  pagination: IPage.IPagination;

  /**
   * Array of IFavorite objects representing user's favorited entries on the
   * current page.
   */
  data: IFavorite[];
};
